import { ToolHeader } from './ToolHeader.js';
import { ColorList } from './ColorList.js';
import { ColorForm } from './ColorForm';
import { ToolFooter } from './ToolFooter';
import { ManageList } from '../hooks/manageList';

export const ColorTool = props =>{

    const [ colors, addColor ] = ManageList([...props.colors]);

    return(
        <>
            <ToolHeader headerText="Color Tool" />
            <ColorList colors={colors} />
            <ColorForm buttonText="Add Color" onSubmitColor={addColor}></ColorForm>
            <ToolFooter footerText="The above section displays color details"></ToolFooter>
        </>
    )
};

ColorTool.defaultProps = {
    colors: [],
}
