import { useState } from "react";

export const ColorTool = (props) =>{

    const [ colors, setColors ] = useState([...props.colors]);

    const [ colorForm, setColorForm ] = useState({
        name: '',
        hexcode: '',
    });

    const change = e => {
        setColorForm({
            //object spread opeartor
            ...colorForm,
            //compted property
            [ e.target.name ]: e.target.value, 
        });
    }

    console.log(colorForm);

    const addColor = () => {
        setColors([
            ...colors,
            {
                ...colorForm,
                id: Math.max(...colors.map(c => c.id) , 0) + 1,
            }
        ])
    };

    return(
        <>
            <header>
                <h1>Color Tool</h1>
            </header>
            <ul>
                {colors.map(color => <li key={color.id}>{color.name}</li>)}
            </ul>

            <form>
                <label>
                    Name:
                    <input type="text" name="name" value={colorForm.name} onChange={change}></input>
                </label>
                &emsp;
                <label>
                    Hexcode:
                    <input type="text" name="hexcode" value={colorForm.hexcode} onChange={change}></input>
                </label>
                <button type="button" onClick={addColor} >Add Color</button>
            </form>
        </>
    )
};
