import  {useDispatch, useSelector } from 'react-redux';
import { bindActionCreators } from 'redux';
import {
    createAddColorAction,
    createDeleteColorAction,
    createSortColorAction
} from '../actions/colorToolActions';

const sortColors = (unsortedItems, sortDir) => {
    const items = [...unsortedItems];
    console.log("here")
    console.log(sortDir)
    console.log(items);
    return items.sort((a,b) => {
        if(a['id'] > b['id']){
            if(sortDir === 'Ascending') //SORT_ASC
                return 1;
            else
                return -1;
        }
        else if(a['id'] < b['id']){
            if(sortDir === 'Ascending') //SORT_ASC
                return -1;
            else
                return 1;
        }
        else
            return 0;
    });
}

export const useColorToolStore = () => {
    const colors = useSelector(state => state.colors);
    const sortDir = useSelector(state => state.sortDir);

    const dispatch = useDispatch();

    const boundActions = bindActionCreators({
        addColor: createAddColorAction,
        deleteColor: createDeleteColorAction,
        sortColor: createSortColorAction
    }, dispatch);

    const sortedColors = sortColors(colors, sortDir);

    return {
        colors,
        sortDir,
        ...boundActions
    };

};
