const sortItems = (unsortedItems, sortCol, sortDir) => {

    const items = [...unsortedItems];

    return items.sort((a,b) => {
        if(a[sortCol] > b[sortCol]){
            if(sortDir === '') //SORT_ASC
                return 1;
            else
                return -1;
        }
        else if(a[sortCol] < b[sortCol]){
            if(sortDir === '') //SORT_ASC
                return -1;
            else
                return 1;
        }
        else
            return 0;
    });
}

export const useCarToolStore = () => {

    const cars = useSelector(state => state.cars);
    const sortCol = useSelector(state => state.sortCol);
    const sortDir = useSelector(state => state.sortDir);
    const editCarId = useSelector(state => state.editCarId);

    sortItems(cars, sortCol, sortDir);

    const dispatch = useDispatch();

    const boundActions = bindActionCreators({
        addCar: createAddCarAction,
        deleteCar: createDeleteCarAction,
        editCar: createEditCarAction,
        sortCar: createSortCarAction,
        saveCar: createSaveCarAction
    }, dispatch);

    return {
        cars,
        sortCol,
        sortDir,
        editCarId,
        ...boundActions
    };
};
