export const ADD_CAR = 'ADD';
export const EDIT_CAR = 'EDIT';
export const SORT_CAR = 'SORT';
export const DELETE_CAR = 'DELETE';

export const createAddCarAction = car => ({ type: ADD_CAR, payload: {car} });

export const createEditCarAction = car => ({ type: ADD_CAR, payload: {car} });

export const createDeleteCarAction = carID => ({ type: ADD_CAR, payload: {carID} });

export const createSortCarAction = (sortCol, sortDir) => ({ type: ADD_CAR, payload: {sortCol,sortDir} });
