import { ADD_ACTION, 
        SUBTRACT_ACTION,
        MULTIPLY_ACTION, 
        DIVIDE_ACTION, 
        CLEAR_ACTION,
        DELETE_HISTORY } from '../actions/calcToolActions';

export const calcToolReducer = (state = {result:0, history: []}, action) => {

    switch(action.type){
        case ADD_ACTION:
            return {
                ...state,
                result: state.result + action.payload.value,
                history: [...state.history,{
                    id: Math.max(...state.history.map(item => item.id) , 0) + 1,
                    value: action.payload.value,
                    type: ADD_ACTION
                }]
            };
        case SUBTRACT_ACTION:
            return {
                ...state,
                result: state.result - action.payload.value,
                history: [...state.history,{
                    id: Math.max(...state.history.map(item => item.id) , 0) + 1,
                    value: action.payload.value,
                    type: SUBTRACT_ACTION
                }]
            };
        case MULTIPLY_ACTION:
            return {
                ...state,
                result: state.result * action.payload.value,
                history: [...state.history,{
                    id: Math.max(...state.history.map(item => item.id) , 0) + 1,
                    value: action.payload.value,
                    type: MULTIPLY_ACTION
                }]
            };
        case DIVIDE_ACTION:
            return {
                ...state,
                result: state.result / action.payload.value,
                history: [...state.history,{
                    id: Math.max(...state.history.map(item => item.id) , 0) + 1,
                    value: action.payload.value,
                    type: DIVIDE_ACTION
                }]
            };
        case CLEAR_ACTION:
            return {
                ...state,
                history: [],
                result: 0,
            };
        case DELETE_HISTORY:
            console.log(action.payload.value);
            return {
                ...state,
                history: [...state.history.filter(h => h.id !== action.payload.value)]
            };
        default:
            return state;
    }
};
