import { BrowserRouter as Router, Route, Link} from 'react-router-dom';
//import { HelloWorld } from './components/HelloWorld';
import { CarTool } from './CarTool';
import { ColorToolStoreProvider } from '../contexts/colorToolStoreContext';
import { ColorTool } from './ColorTool';
import { CarToolStoreProvider } from '../contexts/carToolStoreContext';
import { ToolHeader } from './ToolHeader';
import { ToolFooter } from './ToolHeader';


// //function declaration
// function HelloWorld(){

// }

// //function expression
// const HelloWorld2 = function(){

// };

const colorList = [
  {id: 1, name: 'red', hexcode: 'ff0000'},
  {id: 2, name: 'green', hexcode: '00ff00'},
  {id: 3, name: 'blue', hexcode: '0000ff'}
];

const carList = [
  {id: 1, make: 'Lexus', model: 'NX 300', year: 2021, color: 'Atomic Silver', price: 53000, isEditable: false},
  {id: 2, make: 'Tesla', model: 'Model 3', year: 2021, color: 'Red', price: 72000, isEditable: false}
];

export const App = () =>{
    return (
        <Router path="/">
          <Layout>
            <nav>
                <ul>
                <li><Link to="/">Home</Link></li>
                <li><Link to="/color-tool">Color Tool</Link></li>
                <li><Link to="/car-tool">Car Tool</Link></li>
                </ul>
            </nav>
            <Route path="/" exact>
                <h1>Home Page</h1>
            </Route>
            <Route path="/color-tool">
                <ColorToolStoreProvider colors={colorList}>
                <ColorTool />
                </ColorToolStoreProvider>
            </Route>
            <Route path="/car-tool">
                <CarToolStoreProvider cars={carList}>
                <CarTool />
                </CarToolStoreProvider>
            </Route>
          </Layout>
        </Router>
      )
}
