import { ADD_ACTION, SUBTRACT_ACTION, MULTIPLY_ACTION, DIVIDE_ACTION, CLEAR_ACTION} from '../actions/calcToolActions';

export const calcToolReducer = (state = {result:0, history: []}, action) => {

    switch(action.type){
        case ADD_ACTION:
            const historyCopy = [...state.history.p];
            historyCopy.push({
                id: 1,
                value: action.payload.value,
                type: '+'
            })
            return {
                ...state,
                result: state.result + action.payload.value,
                history: historyCopy
            };
        case SUBTRACT_ACTION:
            return {
                ...state,
                result: state.result - action.payload.value,
            };
        case MULTIPLY_ACTION:
            return {
                ...state,
                result: state.result * action.payload.value,
            };
        case DIVIDE_ACTION:
            return {
                ...state,
                result: state.result / action.payload.value,
            };
        case CLEAR_ACTION:
            return {
                ...state,
                result: 0,
            };
        default:
            return state;
    }
};
