import { ToolHeader } from './ToolHeader.js';
import { ColorList } from './ColorList.js';
import { ColorForm } from './ColorForm';
import { ToolFooter } from './ToolFooter';
import { useColorTool, useColorToolStore } from '../hooks/useColorToolStore';


export const ColorTool = props =>{

    const store = useColorToolStore([...props.colors]);
    return(
        <>
            <ToolHeader headerText="Color Tool" />
            <ColorList colors={store.colors} onDeleteColor={store.removeColor}/>
            <ColorForm
                addColorButtonText="Add Color" 
                onSubmitColor={store.addColor}
            />
            <button type="button" onClick={store.updateSortColor}>Current Sort : {store.sortCol} - {store.sortDirection}</button>
            <ToolFooter footerText="The above section displays color details"></ToolFooter>
        </>
    )
};

ColorTool.defaultProps = {
    colors: [],
}
