import { ADD_ACTION, SUBTRACT_ACTION, MULTIPLY_ACTION, DIVIDE_ACTION, CLEAR_ACTION} from '../actions/calcToolActions';

export const calcToolReducer = (state = {result:0, history: []}, action) => {

    switch(action.type){
        case ADD_ACTION:
            return {
                ...state,
                result: state.result + action.payload.value,
                history: [...state.history,{
                    id: Math.max(...state.history.map(item => item.id) , 0) + 1,
                    value: action.payload.value,
                    type: ADD_ACTION
                }]
            };
        case SUBTRACT_ACTION:
            state.history.push({
                id: Math.max(...state.history.map(item => item.id) , 0) + 1,
                value: action.payload.value,
                type: '-'
            });
            return {
                ...state,
                result: state.result - action.payload.value
            };
        case MULTIPLY_ACTION:
            state.history.push({
                id: Math.max(...state.history.map(item => item.id) , 0) + 1,
                value: action.payload.value,
                type: '*'
            });
            return {
                ...state,
                result: state.result * action.payload.value
            };
        case DIVIDE_ACTION:
            state.history.push({
                id: Math.max(...state.history.map(item => item.id) , 0) + 1,
                value: action.payload.value,
                type: '/'
            });
            return {
                ...state,
                result: state.result / action.payload.value
            };
        case CLEAR_ACTION:
            return {
                ...state,
                history: [],
                result: 0,
            };
        default:
            return state;
    }
};
