import { ADD_ACTION, 
    SUBTRACT_ACTION,
    MULTIPLY_ACTION, 
    DIVIDE_ACTION
} from '../actions/calcToolActions';

export const calcToolStoreReducer = state => {
    var result = 0;
    state.history.forEach(h => {
        switch(h.type){
            case ADD_ACTION:
                result = result + h.value;
                break;
             case SUBTRACT_ACTION:
                result = result - h.value;
                break;
             case MULTIPLY_ACTION:
                result = result * h.value;
                break;
             case DIVIDE_ACTION:
                result = result / h.value;
                break;
            default:
                break;
        }
    });
    return result;
};
