    return (
        <Router path="/">
          <Layout>
            <ToolHeader headerText="The Tools" />  
            <nav>
                <ul className="menu">
                <li className="menu-item"><Link to="/">Home</Link></li>
                <li className="menu-item"><Link to="/color-tool">Color Tool</Link></li>
