                <li className="menu-item"><Link to="/calc-tool">Calc Tool</Link></li>
                </ul>
            </nav>
            <main>
                <Route path="/" exact>
                    <h1>Home Page</h1>
                </Route>
                <Route path="/color-tool">
                    <Provider store={colorToolStore}>
                      <ColorTool />
