const isEmpty = arr => !Array.isArray(arr) || arr.length === 0;

export const ColorList = props => {
    return(
        <ul>
            { isEmpty(props.cars)
                ? <li>No Colors</li> : 
                props.colors.map(color => <li key={color.id}>{color.name}</li>)}
        </ul>
    );
};

ColorList.defaultProps = {
    colors: [],
}
