import { useState } from 'react';

export const manageList = initialList => {

    const [list, updateList ] = useState(initialList);

    const addToList = newItem => {
        updateList([
            ...list,
            {
                ...newItem,
                id: Math.max(...list.map(item => item.id) , 0) + 1,
            }
        ]);
    };

    const deleteItem = itemId => {
        updateList(
            list.filter(item => item.id !== itemId)
        );
    }

    const updateItem = updatedItem => {
        const newList = [...list];
        const updatedItemIndex = newList.findIndex(item => item.id === updatedItem.id);
        newList[updatedItemIndex] = updatedItem;
        updateList(newList);
    }

    return [ addToList, deleteItem, updateItem ];
};
