export const CarEditRow = props =>{

    const [carEditForm, setCarEditForm] = useState({...props.car});

    const change = e => {
        setCarEditForm({
            ...carEditForm,
            //[ e.target.name ]: e.target.value,
            [e.target.name]: e.target.type === 'number' ? parseInt(e.target.value, 10) : e.target.value,
        });
    }
    return(
        <tr key={carEditForm.id}>
            <td>{carEditForm.id}</td>
            <td><input type="text" value={carEditForm.make}></input></td>
            <td><input type="text" value={carEditForm.model}></input></td>
            <td><input type="text" value={carEditForm.year}></input></td>
            <td><input type="text" value={carEditForm.color}></input></td>
            <td><input type="text" value={carEditForm.price}></input></td>
            <td>
                <button type="button" onClick={onClickDelete}>Save</button>
                <button type="button" onClick={onClickDelete}>Cancel</button>
            </td>
        </tr>
    );
};
