import { useState } from "react";

export const ColorTool = (props) =>{

    const [ colorForm, setColorForm ] = useState({
        name: '',
        hexcode: '',
    });

    return(
        <>
            <header>
                <h1>Color Tool</h1>
            </header>
            <ul>
                {props.colors.map(color => <li key={color.id}>{color.name}</li>)}
            </ul>

            <form>
                <label>
                    Name:
                    <input type="text"></input>
                </label>
                &emsp;
                <label>
                    Hexcode:
                    <input type="text"></input>
                </label>
            </form>
        </>
    )
};
