import { ToolHeader } from './ToolHeader.js';
import { ColorList } from './ColorList.js';
import { ColorForm } from './ColorForm';
import { ToolFooter } from './ToolFooter';
import { useSortedList, SORT_ASC, SORT_DESC } from '../hooks/useSortedList.js';


export const ColorTool = props =>{

    const [ 
        colors, 
        addColor, removeColor, ,
        sortCol, setSortCol,
        sortDirection, setSortDirection
    ] = useSortedList([...props.colors], 'id', SORT_ASC);

    const updateSortColor = () => {
        if(sortCol === 'id'){
            setSortCol('name');
            setSortDirection(SORT_ASC);
        }
        else if(sortCol === 'name' && sortDirection === SORT_ASC){
            setSortDirection(SORT_DESC);
        }
        else{
            setSortCol('id');
            setSortDirection(SORT_ASC);
        }
    };

    return(
        <>
            <ToolHeader headerText="Color Tool" />
            <ColorList colors={colors} onDeleteColor={removeColor}/>
            <ColorForm
                addColorButtonText="Add Color" 
                onSubmitColor={addColor}
            />
            <button type="button" onClick={updateSortColor}>Current Sort : {sortCol} - {sortDirection}</button>
            <ToolFooter footerText="The above section displays color details"></ToolFooter>
        </>
    )
};

ColorTool.defaultProps = {
    colors: [],
}
