import { useState } from "react";
import { ToolHeader } from "./ToolHeader.js";
import { CarTable } from "./CarTable.js";
import { CarForm } from "./CarForm";
import { ToolFooter } from './ToolFooter';

export const CarTool = props =>{

    const [ cars, setCars ] = useState([...props.cars]);

    const addCar = (newCar) =>{
        setCars([
            ...cars,
            {
                ...newCar,
                id: Math.max(...cars.map(car => car.id) , 0) + 1,
            }
        ]);
    };

    const editCar = (editCarId) => {
        setCars(
            cars.forEach(car => car.id === editCarId ? car.isEditable=true : car.isEditable=false)
        );
    }

    const deleteCar = (removedCarID) => {
        setCars(
            cars.filter(car => car.id !== removedCarID)
        );
    }

    console.log(cars);

    return(
        <>
            <ToolHeader headerText="Car Tool" />
            <CarTable cars={cars} onDeleteCar={deleteCar} />
            <br></br>
            <CarForm buttonText="Add Car" onSubmitCar={addCar}></CarForm>
            <ToolFooter footerText="The above section displays car details"></ToolFooter>
        </>
    )
};

CarTool.defaultProps = {
    cars: [],
}
