const isEmpty = arr => !Array.isArray(arr) || arr.length === 0;

export const ColorList = props => {
    return(
        <ul>
            { isEmpty(props.colors)
                ? <li>No Colors</li> : 
                props.colors.map(color => {
                        return(<>
                        <li key={color.id}>{color.name}</li>&emsp;
                        <button type="button" onClick={() => props.onDeleteColor(props.car.id)} >Delete</button>
                        </>);
                    }

                )}
        </ul>
    );
};

ColorList.defaultProps = {
    colors: [],
}
