import { useState } from "react";
import { ToolHeader } from "./ToolHeader.js";

export const CarTool = (props) =>{

    const [ cars, setCars ] = useState([...props.cars]);

    const [carForm, setCarForm ] = useState({
        make: '',
        model: '',
        year: 1990,
        color: '',
        price: 2300,
    });

    const change = e => {
        setCarForm({
            ...carForm,
            //[ e.target.name ]: e.target.value,
            [ e.target.name ]: e.target.type === 'number' ? parseInt(e.target.value, 10) : e.target.value,
        });
    }

    const addCar = () =>{
        setCars([
            ...cars,
            {
                ...carForm,
                id: Math.max(...cars.map(car => car.id) , 0) + 1,
            }
        ]);

        setCarForm({
            make: '',
            model: '',
            year: 1990,
            color: '',
            price: 2300,
        });
    };

    console.log(cars);

    return(
        <>
            <ToolHeader headerText="Car Tool"></ToolHeader>
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>MAKE</th>
                        <th>MODEL</th>
                        <th>YEAR</th>
                        <th>COLOR</th>
                        <th>PRICE</th>
                    </tr>
                </thead>
                <tbody>
                    {cars.map(car => 
                                        <tr key={car.id}>
                                            <td>{car.id}</td>
                                            <td>{car.make}</td>
                                            <td>{car.model}</td>
                                            <td>{car.year}</td>
                                            <td>{car.color}</td>
                                            <td>{car.price}</td>
                                        </tr>
                                    )
                    }
                </tbody>
            </table>
            <br></br>
            <form>
                <label>
                    MAKE:
                    <input type="text" name="make" value={carForm.make} onChange={change} ></input>
                </label>
                &emsp;
                <label>
                    MODEL:
                    <input type="text" name="model" value={carForm.model} onChange={change} ></input>
                </label>
                &emsp;
                <label>
                    YEAR:
                    <input type="number" name="year" value={carForm.year} onChange={change} ></input>
                </label>
                &emsp;
                <label>
                    COLOR:
                    <input type="text" name="color" value={carForm.color} onChange={change} ></input>
                </label>
                &emsp;
                <label>
                    PRICE:
                    <input type="number" name="price" value={carForm.price} onChange={change} ></input>
                </label>
                <button type="button" onClick={addCar} >Add Car</button>
            </form>
        </>
    )
};
