import { combineReducers } from "redux";

import {
 ADD_COLOR,
 DELETE_COLOR,
 SORT_COLOR,

} from '../actions/colorToolActions';

const SORT_ASCENDING = "Ascending";
const SORT_DESCENDING = "Descending";

const colorList = [
    {id: 1, name: 'red', hexcode: 'ff0000'},
    {id: 2, name: 'green', hexcode: '00ff00'},
    {id: 3, name: 'blue', hexcode: '0000ff'}
];

const colorReducer = (colors = colorList, action) => {
    switch(action.type){
        case ADD_COLOR:
            return [...colors,{
                ...action.payload.color,
                id: Math.max(...colors.map(c => c.id) , 0) + 1,
            }];
        case DELETE_COLOR:
            return colors.filter(color => color.id !== action.payload.colorID);
        default:
            return colors;
    }
};

const sortDirectionReducer = (sortDir = SORT_ASCENDING, action) => {
    if(action.type === SORT_COLOR){
        if(sortDir === SORT_ASCENDING)
            sortDir = SORT_DESCENDING;
        else
            sortDir = SORT_ASCENDING;
    }
    return sortDir;
}

const sortColReducer = (sortCol = 'id', action) => {

    return sortCol;
};

export const colorToolReducer = combineReducers({
    colors: colorReducer,
    sortDir: sortDirectionReducer
});
