import { combineReducers } from "redux";

import {
    ADD_CAR,
    DELETE_CAR,
    EDIT_CAR,
    SORT_CAR,
    SAVE_CAR
} from '../actions/carToolActions';

const sortItems = (unsortedItems, sortCol, sortDir) => {

    const items = [...unsortedItems];

    return items.sort((a,b) => {
        if(a[sortCol] > b[sortCol]){
            if(sortDir === '') //SORT_ASC
                return 1;
            else
                return -1;
        }
        else if(a[sortCol] < b[sortCol]){
            if(sortDir === '') //SORT_ASC
                return -1;
            else
                return 1;
        }
        else
            return 0;
    });
}

const carList = [
    {id: 1, make: 'Lexus', model: 'NX 300', year: 2021, color: 'Atomic Silver', price: 53000, isEditable: false},
    {id: 2, make: 'Tesla', model: 'Model 3', year: 2021, color: 'Red', price: 72000, isEditable: false}
  ];

const carReducer = (cars = carList, action) => {
    switch(action.type){
        case ADD_CAR:
            return [...cars,{
                ...action.payload.car,
                id: Math.max(...cars.map(c => c.id) , 0) + 1,
            }];
        case DELETE_CAR:
            return cars;
        case EDIT_CAR:
            return cars;
        default:
            return cars;
    }
}

const editCarIdReducer = (editCarID = -1, action) => {
    switch(action.type){
        case ADD_CAR:
            return -1;
        case DELETE_CAR:
            return -1;
        case EDIT_CAR:
            return editCarID;

    }
}
export const carToolReducer = (state= {cars: [], sortCol: '', sortDir: '', editCarId: -1}, action) => {

    switch(action.type){
        case ADD_CAR:
            return {
                ...state,
                cars: [...state.cars,{
                    ...action.payload.car,
                    id: Math.max(...state.cars.map(c => c.id) , 0) + 1,
                }],
                editCarId: -1
            };
        default:
            return state;
    }
};
