import { useState } from "react";
import { useForm } from "../hooks/useForm";

export const ColorForm = props => {

    const [colorForm, setColorForm] = useState({
        name: '',
        hexcode: '',
    });

    const change = e => {
        setColorForm({
            //object spread opeartor
            ...colorForm,
            //compted property
            [e.target.name]: e.target.value,
        });
    }

    const submitColor = () => {

        props.onSubmitColor({...colorForm});

        setColorForm({
            name: '',
            hexcode: '',
        });
    };

    return (
        <form>
            <label>
                Name:
                    <input type="text" name="name" value={colorForm.name} onChange={change}></input>
            </label>
            &emsp;
                <label>
                Hexcode:
                    <input type="text" name="hexcode" value={colorForm.hexcode} onChange={change}></input>
            </label>
            <button type="button" onClick={submitColor} >{props.buttonText}</button>
        </form>
    );
};
