import { useState } from "react";
import { ToolHeader } from "./ToolHeader.js";
import { CarTable } from "./CarTable.js";
import { CarForm } from "./CarForm";
import { ToolFooter } from './ToolFooter';
import { useSortedList, SORT_ASC, SORT_DESC } from '../hooks/useSortedList';

export const CarTool = props =>{


    const [ editCarId, setEditCarId ] = useState(-1);

    const [ 
        cars, addCarToList, deleteCarFromList, saveCarToList,
        sortCol, setSortCol,
        sortDir, setSortDir
    ] = useSortedList([...props.cars], 'id', SORT_ASC);

    const addCar = (newCar) =>{
        addCarToList(newCar);
        setEditCarId(-1);
    };

    const deleteCar = (removedCarID) => {
        deleteCarFromList(removedCarID);
        setEditCarId(-1);
    }

    const saveCar = (updatedCar) => {
        saveCarToList(updatedCar);
        setEditCarId(-1);
    }

    const updateSort = (newSortColName) => {
        if(sortCol === newSortColName){
            if(sortDir === SORT_ASC)
                setSortDir(SORT_DESC);
            else
                setSortDir(SORT_ASC);
        }else{
            setSortDir(SORT_ASC);
        }
        setSortCol(newSortColName);
    }

    return(
        <>
            <ToolHeader headerText="Car Tool" />
            <CarTable cars={cars} 
                editCarID={editCarId} 
                onEditCarID={setEditCarId} 
                onDeleteCar={deleteCar} 
                onClickSave={saveCar} 
                onClickSort={updateSort}/>
            <br></br>
            <CarForm buttonText="Add Car" onSubmitCar={addCar}></CarForm>
            <ToolFooter footerText="The above section displays car details"></ToolFooter>
        </>
    )
};

CarTool.defaultProps = {
    cars: [],
}
