import { ADD_ACTION, 
    SUBTRACT_ACTION,
    MULTIPLY_ACTION, 
    DIVIDE_ACTION
} from '../actions/calcToolActions';

export const calcToolStoreReducer = state => {
    return state.history.reduce( (result,entry) => {
        switch(entry.type){
            case ADD_ACTION:
                result = result + h.value;
                break;
             case SUBTRACT_ACTION:
                result = result - h.value;
                break;
             case MULTIPLY_ACTION:
                result = result * h.value;
                break;
             case DIVIDE_ACTION:
                result = result / h.value;
                break;
            default:
                break;
        }
    }, 0);
}

export const operationCountReducer = state => {
    var addCount =0;
    var subCount=0;
    var mulCount =0;
    var divideCount = 0;
    state.history.forEach(h => {
        switch(h.type){
            case ADD_ACTION:
                addCount++;
                break;
             case SUBTRACT_ACTION:
                subCount++;
                break;
             case MULTIPLY_ACTION:
                mulCount++;
                break;
             case DIVIDE_ACTION:
                divideCount++;
                break;
            default:
                break;
        }
    });
    return {
        addCount: addCount,
        subCount: subCount,
        mulCount: mulCount,
        divideCount: divideCount
    };
};
