import { useState } from 'react';
import { useCalcToolStore } from '../hooks/useCalcToolStore';

export const CalcTool = () => {

    const {
        result, 
        history,
        add,
        subtract,
        multiply,
        divide,
        clear
        } = useCalcToolStore();

    const [ numInput, setNumInput] = useState(0);

    const isEmpty = arr => !Array.isArray(arr) || arr.length === 0;

    return(
        <div>
            <section>
                Result: {result}
            </section>
            <form>
                <label>
                    Num:
                    <input type="number" value={numInput}
                        onChange={
                            ({target: { value } }) => setNumInput(parseInt(value, 10))
                    }
                    />
                </label>
                <fieldset>
                    <button type="button"
                     onClick={() => add(numInput)}>+</button>
                    <button type="button"
                     onClick={() => subtract(numInput)}>-</button>
                    <button type="button"
                     onClick={() => multiply(numInput)}>*</button>
                    <button type="button"
                     onClick={() => divide(numInput)}>/</button>
                    <button type="button"
                     onClick={() => clear()}>Clear</button>
                </fieldset>
                <ul>
                    { isEmpty(history)
                    ? <li>No History</li> : 
                    history.map(h => {
                            return(
                                <li key={h.id}>{h.id}&emsp;{h.type}&emsp;
                                </li>
                            );
                        }

                    )}
                </ul>
            </form>
        </div>
    )
};
