import { ToolHeader } from './ToolHeader.js';
import { ColorList } from './ColorList.js';
import { ColorForm } from './ColorForm';
import { ToolFooter } from './ToolFooter';
import { useList } from '../hooks/useList';
import { useState } from 'react';
import { useSortedList, SORT_ASC, SORT_DESC } from '../hooks/useSortedList.js';


export const ColorTool = props =>{

    const [ 
        colors, 
        addColor, removeColor, 
        sortCol, setSortCol,
        sortDirection, setSortDirection
    ] = useSortedList([...props.colors]);

    const [ sortCriteria, setSortCriteria ] = useState('id');

    const sortColors = () => {
        const sortedColors = [...colors];
        if(sortCriteria === 'id'){
            sortedColors.sort(colorSortIdAscending);
        }
        else if(sortCriteria === 'name ascending'){
            sortedColors.sort(colorSortNameAscending);
        }
        else{
            sortedColors.sort(colorSortNameDecending);
        }
        return sortedColors;
    };

    const updateSortColor = () => {
        if(sortCriteria === 'id'){
            setSortCriteria('name ascending');
        }
        else if(sortCriteria === 'name ascending'){
            setSortCriteria('name decending');
        }
        else{
            setSortCriteria('id');
        }
    };

    const colorSortNameAscending = (a,b) => {
        if(a.name > b.name)
            return 1;
        else if(a.name < b.name)
            return -1;
        else
            return 0;
    }

    const colorSortNameDecending = (a,b) => {
        if(a.name > b.name)
            return -1;
        else if(a.name < b.name)
            return 1;
        else
            return 0;
    }

    const colorSortIdAscending = (a,b) => {
        if(a.id > b.id)
            return 1;
        else if(a.id < b.id)
            return -1;
        else
            return 0;
    }

    const sortButtonText = "Current Sort : " + {sortCol} + " - " + {sortDirection}; 

    return(
        <>
            <ToolHeader headerText="Color Tool" />
            <ColorList colors={sortColors(colors)} onDeleteColor={removeColor}/>
            <ColorForm
                addColorButtonText="Add Color" 
                sortColorButtonText="Sort Color" 
                onSubmitColor={addColor}
                sortCritera={sortButtonText}
                onSortColor={updateSortColor}
            />
            <ToolFooter footerText="The above section displays color details"></ToolFooter>
        </>
    )
};

ColorTool.defaultProps = {
    colors: [],
}
