import { ToolHeader } from "./ToolHeader.js";
import { CarTable } from "./CarTable.js";
import { CarForm } from "./CarForm";
import { ToolFooter } from './ToolFooter';
import { useCarToolStoreContext } from '../contexts/carToolStoreContext';
import { CarFormContainer } from './CarFormContainer';
import { CarTableContainer } from './CarTableContainer';

export const CarTool = () =>{

    const carToolStore = useCarToolStoreContext();

    return(
        <>
            <ToolHeader headerText="Car Tool" />
            <CarTableContainer />
            <br></br>
            <CarFormContainer />
            <ToolFooter footerText="The above section displays car details"></ToolFooter>
        </>
    )
};

CarTool.defaultProps = {
    cars: [],
}
