export const REFRESH_COLOR_REQUEST_ACTION = "REFRESH_COLOR_REQUEST_ACTION";
export const REFRESH_COLOR_DONE_ACTION = "REFRESH_COLOR_DONE_ACTION";
export const ADD_COLOR = "Add_Color";
export const DELETE_COLOR = "Delete_Color";
export const SORT_COLOR = "Sort_Color";

export const createRefreshColorsRequestAction = ({type: REFRESH_COLOR_REQUEST_ACTION});

export const createRefreshColorsDoneAction = (
    {type: REFRESH_COLOR_REQUEST_ACTION, payload: {colors} 
});

export const refreshColors = () => {
    //This function is the function action object
    //when middleware invokes this function is passes in the store's dispatch method
    return dispatch => {
        dispatch(createRefreshColorsDoneAction);
    };
};

export const createAddColorAction = color => ({ type: ADD_COLOR, payload: {color} });

export const createDeleteColorAction = colorID => ({ type: DELETE_COLOR, payload: {colorID} });

export const createSortColorAction = () => ({type: SORT_COLOR});
