import { useState } from 'react';
import { useCalcToolStore } from '../hooks/useCalcToolStore';

export const CalcTool = () => {

    const {
        result, 
        add,
        subtract,
        multiply,
        divide
        } = useCalcToolStore();

    const [ numInput, setNumInput] = useState(0);

    return(
        <div>
            <section>
                Result: {result}
            </section>
            <form>
                <label>
                    Num:
                    <input type="number" value={numInput}
                        onChange={
                            ({target: { value } }) => setNumInput(parseInt(value, 10))
                    }
                    />
                </label>
                <fieldset>
                    <button type="button"
                     onClick={() => add(numInput)}>+</button>
                    <button type="button"
                     onClick={() => subtract(numInput)}>-</button>
                    <button type="button"
                     onClick={() => subtract(numInput)}>*</button>
                    <button type="button"
                     onClick={() => subtract(numInput)}>/</button>
                </fieldset>
            </form>
        </div>
    )
};
