import { combineReducers } from "redux";

import {
    ADD_CAR,
    DELETE_CAR,
    EDIT_CAR,
    SORT_CAR,
    SAVE_CAR
} from '../actions/carToolActions';

const sortItems = (unsortedItems, sortCol, sortDir) => {

    const items = [...unsortedItems];

    return items.sort((a,b) => {
        if(a[sortCol] > b[sortCol]){
            if(sortDir === '') //SORT_ASC
                return 1;
            else
                return -1;
        }
        else if(a[sortCol] < b[sortCol]){
            if(sortDir === '') //SORT_ASC
                return -1;
            else
                return 1;
        }
        else
            return 0;
    });
}

const carList = [
    {id: 1, make: 'Lexus', model: 'NX 300', year: 2021, color: 'Atomic Silver', price: 53000, isEditable: false},
    {id: 2, make: 'Tesla', model: 'Model 3', year: 2021, color: 'Red', price: 72000, isEditable: false}
  ];

const carReducer = (cars = carList, action) => {
    switch(action.type){
        case ADD_CAR:
            return [...cars,{
                ...action.payload.car,
                id: Math.max(...cars.map(c => c.id) , 0) + 1,
            }];
        case DELETE_CAR:
            console.log(action)
            return cars.filter(car => car.id !== action.payload.carID);
        case SAVE_CAR:
            return cars;
        default:
            return cars;
    }
}

const editCarIdReducer = (editCarId = -1, action) => {
    switch(action.type){
        case ADD_CAR:
            return -1;
        case DELETE_CAR:
            return -1;
        case EDIT_CAR:
            console.log(action.payload.car);
            editCarId = -1
            return editCarId;
        case SAVE_CAR:
            return -1;
        default:
            return -1;

    }
}

const sortColReducer = (sortCol = 'id', action) => {
    return sortCol;
}

const sortDirReducer = (sortDir = '', action) => {
    return sortDir;
}

export const carToolReducer = combineReducers({
    cars: carReducer,
    editCarID: editCarIdReducer,
    sortCol: sortColReducer,
    sortDir: sortDirReducer
})
