import { ADD_ACTION, 
        SUBTRACT_ACTION,
        MULTIPLY_ACTION, 
        DIVIDE_ACTION, 
        CLEAR_ACTION,
        DELETE_HISTORY } from '../actions/calcToolActions';
import { combineReducer } from 'react';

const resultReducer = (result=0, action) => {

    switch(action.type){
        case ADD_ACTION:
            return result + action.payload.value;
        case SUBTRACT_ACTION:
            return result - action.payload.value;
        case MULTIPLY_ACTION:
            return result * action.payload.value;
        case DIVIDE_ACTION:
            if(action.payload.value === 0){
                console.log("here");
                return {
                    ...state,
                    errorMessage: 'Divide By 0 is not allowed',
                };
            }else{
                return {
                    ...state,
                    errorMessage: '',
                    result: state.result / action.payload.value,
                    history: [...state.history,{
                        id: Math.max(...state.history.map(item => item.id) , 0) + 1,
                        value: action.payload.value,
                        type: DIVIDE_ACTION
                    }]
                };
            }
        case CLEAR_ACTION:
            return 0;
        default:
            return result;
    }
}

const historyReducer = (history =[] , action) => {
    if([ADD_ACTION,SUBTRACT_ACTION,MULTIPLY_ACTION,DIVIDE_ACTION].includes(action.type)){
        return [...state.history,{
            id: Math.max(...state.history.map(item => item.id) , 0) + 1,
            value: action.payload.value,
            type: MULTIPLY_ACTION
        }];
    }
}
export const calcToolReducer = (state = {
                                    result:0,
                                    history: [],
                                    errorMessage: ''
                                }, action) => {

    return {
        ...state,
        result: resultReducer(state.result, action),
