import { combineReducers } from "redux";

import {
    ADD_CAR,
    DELETE_CAR,
    EDIT_CAR,
    SORT_CAR,
    SAVE_CAR
} from '../actions/carToolActions';

const carList = [
    {id: 1, make: 'Lexus', model: 'NX 300', year: 2021, color: 'Atomic Silver', price: 53000, isEditable: false},
    {id: 2, make: 'Tesla', model: 'Model 3', year: 2021, color: 'Red', price: 72000, isEditable: false}
  ];

export const SORT_ASC = "Ascending";
export const SORT_DESC = "Descending";

const carReducer = (cars = carList, action) => {
    switch(action.type){
        case ADD_CAR:
            return [...cars,{
                ...action.payload.car,
                id: Math.max(...cars.map(c => c.id) , 0) + 1,
            }];
        case DELETE_CAR:
            console.log(action)
            return cars.filter(car => car.id !== action.payload.carID);
        case SAVE_CAR:
            const carsCopy = [...cars];
            const updatedItemIndex = carsCopy.findIndex(car => car.id === action.payload.car.id);
            carsCopy[updatedItemIndex] = action.payload.car;
            return carsCopy;
        default:
            return cars;
    }
}

const editCarIdReducer = (editCarId = -1, action) => {
    switch(action.type){
        case ADD_CAR:
            return -1;
        case DELETE_CAR:
            return -1;
        case EDIT_CAR:
            console.log(action.payload.car);
            editCarId = action.payload.car;
            return editCarId;
        case SAVE_CAR:
            return -1;
        default:
            return -1;

    }
}

const sortColReducer = (sortCol = 'id', action) => {
    return action.payload.sortCol;
}

const sortDirReducer = (sortDir = SORT_ASC, action) => {
    if(sortDir === SORT_ASC)
        return SORT_DESC;
    else
        return SORT_ASC;
}

export const carToolReducer = combineReducers({
    cars: carReducer,
    editCarId: editCarIdReducer,
    sortCol: sortColReducer,
    sortDir: sortDirReducer
})
