import { combineReducers } from "redux";

import {
    ADD_CAR,
    DELETE_CAR,
    EDIT_CAR,
    SORT_CAR,
    SAVE_CAR,
    SORT_ASC,
    SORT_DESC
} from '../actions/carToolActions';

const carList = [
    {id: 1, make: 'Lexus', model: 'NX 300', year: 2021, color: 'Atomic Silver', price: 53000, isEditable: false},
    {id: 2, make: 'Tesla', model: 'Model 3', year: 2021, color: 'Red', price: 72000, isEditable: false}
  ];


const carReducer = (cars = carList, action) => {
    switch(action.type){
        case ADD_CAR:
            return [...cars,{
                ...action.payload.car,
                id: Math.max(...cars.map(c => c.id) , 0) + 1,
            }];
        case DELETE_CAR:
            console.log(action)
            return cars.filter(car => car.id !== action.payload.carID);
        case SAVE_CAR:
            const carsCopy = [...cars];
            const updatedItemIndex = carsCopy.findIndex(car => car.id === action.payload.car.id);
            carsCopy[updatedItemIndex] = action.payload.car;
            return carsCopy;
        default:
            return cars;
    }
}

const editCarIdReducer = (editCarId = -1, action) => {
    switch(action.type){
        case ADD_CAR:
            return -1;
        case DELETE_CAR:
            return -1;
        case EDIT_CAR:
            console.log(action.payload.car);
            editCarId = action.payload.car;
            return editCarId;
        case SAVE_CAR:
            return -1;
        default:
            return -1;

    }
}

const sortColReducer = (sortCol = 'id', action) => {
    console.log(sortCol);
    if(action.type === SORT_CAR){
        sortCol = action.payload.col;
        console.log(action.payload.col);
    }
    return sortCol;
}

const sortDirReducer = (sortDir = SORT_ASC, action) => {
    if(action.type === SORT_CAR){
        if(sortDir === SORT_ASC)
            sortDir = SORT_DESC
        else
            sortDir = SORT_ASC;
    }
    return sortDir;
}

const carsSortReducer = ( carsSort = {sortCol: 'id', sortDir: SORT_ASC}, action) => {
    if(action.type === SORT_CAR){
        if(action.payload.col === carsSort.sortCol){
            if(carsSort.sortDir === SORT_ASC)
                return {...carsSort, sortDir: SORT_DESC};
            else
                return {...carsSort, sortDir: SORT_ASC};
        }
        else{
            return {...carsSort, sortCol: action.payload.col, sortDir: SORT_ASC};
        }
    }
    return carsSort;
};

export const carToolReducer = combineReducers({
    cars: carReducer,
    editCarId: editCarIdReducer,
    carsSort: carsSortReducer
})
