const sortColors = (unsortedItems, sortDir) => {
    const items = [...unsortedItems];
    return items.sort((a,b) => {
        if(a['id'] > b['id']){
            if(sortDir === 'Ascending') //SORT_ASC
                return 1;
            else
                return -1;
        }
        else if(a['id'] < b['id']){
            if(sortDir === 'Ascending') //SORT_ASC
                return -1;
            else
                return 1;
        }
        else
            return 0;
    });
}

export const sortedColorsSelector = state => {
    return  sortColors(state.colors, state.sortDir)
}
