import { useState } from "react";
import { ToolHeader } from './ToolHeader.js';
import { ColorList } from './ColorList.js';

export const ColorTool = (props) =>{

    const [ colors, setColors ] = useState([...props.colors]);

    const [ colorForm, setColorForm ] = useState({
        name: '',
        hexcode: '',
    });

    const change = e => {
        setColorForm({
            //object spread opeartor
            ...colorForm,
            //compted property
            [ e.target.name ]: e.target.value, 
        });
    }

    const addColor = () => {
        setColors([
            ...colors,
            {
                ...colorForm,
                id: Math.max(...colors.map(c => c.id) , 0) + 1,
            }
        ]);

        setColorForm({
            name: '',
            hexcode: '',
        });
    };

    return(
        <>
            <ToolHeader headerText="Color Tool" />

            <ColorList colors={colors} />

            <form>
                <label>
                    Name:
                    <input type="text" name="name" value={colorForm.name} onChange={change}></input>
                </label>
                &emsp;
                <label>
                    Hexcode:
                    <input type="text" name="hexcode" value={colorForm.hexcode} onChange={change}></input>
                </label>
                <button type="button" onClick={addColor} >Add Color</button>
            </form>
        </>
    )
};

ColorTool.defaultProps = {
    colors: [],
}
