const sortItems = (unsortedItems, sortCol, sortDir) => {
    console.log(sortCol);
    console.log(sortDir);

    const items = [...unsortedItems];

    return items.sort((a,b) => {
        if(a[sortCol] > b[sortCol]){
            if(sortDir === 'Ascending') //SORT_ASC
                return 1;
            else
                return -1;
        }
        else if(a[sortCol] < b[sortCol]){
            if(sortDir === '') //SORT_ASC
                return -1;
            else
                return 1;
        }
        else
            return 0;
    });
}

export const CarTableContainer = () => {
    const carToolStore = useCarToolStore();
        return(
        <CarTable cars={sortItems(carToolStore.cars, carToolStore.sortCol, carToolStore.sortDir)} 
                editCarID={carToolStore.editCarId}
                onEditCarID={carToolStore.editCar}
                onDeleteCar={carToolStore.deleteCar} 
                onClickSave={carToolStore.saveCar} 
                onClickSort={carToolStore.sortCar}
                sortButtonText={carToolStore.sortCol + "-" + carToolStore.sortDir} />
    )
}
